INSERT INTO `nideshop_goods_issue` VALUES (1, '家具如何保养？', '摆放在阴凉、干净、通风、温度和光照始终的地方。移动家具时轻抬轻放。装放物品前应先把柜体放平，使其四脚平稳着地。过重的物品不宜放在桌面或桌缘。');
INSERT INTO `nideshop_goods_issue` VALUES (2, '周末发货吗？发货地是哪里？什么快递？', '周末可以发货，发货地址是惠州，小件用的是天天快递，大件发的是五包物流（贝业、易友通）。');
INSERT INTO `nideshop_goods_issue` VALUES (3, '退换货的要求是什么？是否可以七天无理由退换货？', '可以七天无理由退换货，但一定要不影响二次要求，退货的费用由买家承担。');
INSERT INTO `nideshop_goods_issue` VALUES (4, '如何开具发票？', ' 如需开具发票，请在备注里填写详细信息，或在会员管理页面联系客服。');
INSERT INTO `nideshop_goods_issue` VALUES (5, '包运输安装吗？', '全国1161个城区免费配送安装，部分不在五包的地区，需补贴部分费用。具体可咨询客服。');
